import React, { useState } from 'react';
import SearchingAndBacktracking from '../sudoku/Algorithm.js'
import InputBoard from './InputBoard'

var initialInput = [
    [5, 3, 0, 0, 7, 0, 0, 0, 0],
    [6, 0, 0, 1, 9, 5, 0, 0, 0],
    [0, 9, 8, 0, 0, 0, 0, 6, 0],
    [8, 0, 0, 0, 6, 0, 0, 0, 3],
    [4, 0, 0, 8, 0, 3, 0, 0, 1],
    [7, 0, 0, 0, 2, 0, 0, 0, 6],
    [0, 6, 0, 0, 0, 0, 2, 8, 0],
    [0, 0, 0, 4, 1, 9, 0, 0, 5],
    [0, 0, 0, 0, 8, 0, 0, 7, 9]
]

function Board(props) {
    const [state, setState] = useState({
        inputBoard: initialInput, uploadProgress: { status: "None", progress: 0 }
    });

    let prepareInitialInput = (updatedInputBoard) => {
        setState({
            inputBoard: updatedInputBoard
        })
    }

    let updateBoard = (updatedBoard) => {
        setState({
            ...state,
            inputBoard: updatedBoard
        })
    }

    let getBoard = (inputBoard) => {
        var _tr = inputBoard.map((item, index) => {
            return (
                <div className="row" key={index}>
                    {
                        item.map((_item, _index) => {
                            var key = _index + index;
                            return (
                                <div className="col-1 border" key={key} style={{ position: "relative", margin: "2 2 2 2" }}>
                                    {_item === 0 ? '' : _item}
                                </div>
                            )
                        })
                    }
                </div>
            )
        });
        return (
            <div className="container" style={{ margin: "10%" }}>
                {_tr}
            </div>
        );
    }

    return (
        <div className="container">
            <div className="container">
                <div className="row" style={{ margin: "0 0 2vh 0" }}>

                </div>
                <div className="row">
                    <div className="col-5">
                        <InputBoard updateBoard={prepareInitialInput} />
                        <button type="button"
                            onClick={SearchingAndBacktracking.SudokuUsingStack.main.bind(this, { inputBoard: state.inputBoard, updateBoard: updateBoard })} >
                            Solve
                    </button>
                    </div>
                    <div className="col-5">
                        {getBoard(state.inputBoard)}
                    </div>
                </div>
            </div>

        </div>
    )
}

export default Board;
