import React, { useState } from 'react';
import Head from 'next/head';

function Page(props) {
    const [state, setState] = useState({
        board: [
            [],
            [],
            [],
            [],
            [],
            [],
            [],
            [],
            []
        ]
    })

    let updateBoard = (row, col) => (e) => {
        let _board = state.board;
        _board[row][col] = e.target.value;
        setState({
            board: _board
        })
    }
    const { board } = state;

    let makeBoardTable = () => {
        let boardTableRows = [];
        for (var i = 0; i < 10; i++) {
            if (typeof board[i] === 'undefined')
                board[i] = [];
            boardTableRows.push(
                <tr>
                    <td> <input type="text" value={board[i][0]} className="board-input " onChange={updateBoard(i, 0)} /> </td>
                    <td> <input type="text" value={board[i][1]} className="board-input " onChange={updateBoard(i, 1)} /> </td>
                    {/* <td> <input type="text" value={board[i][2]} className="board-input " onChange={updateBoard(i, 2)} /> </td>
                    <td> <input type="text" value={board[i][3]} className="board-input " onChange={updateBoard(i, 3)} /> </td>
                    <td> <input type="text" value={board[i][4]} className="board-input " onChange={updateBoard(i, 4)} /> </td>
                    <td> <input type="text" value={board[i][5]} className="board-input " onChange={updateBoard(i, 5)} /> </td>
                    <td> <input type="text" value={board[i][6]} className="board-input " onChange={updateBoard(i, 6)} /> </td>
                    <td> <input type="text" value={board[i][7]} className="board-input " onChange={updateBoard(i, 7)} /> </td>
                    <td> <input type="text" value={board[i][8]} className="board-input " onChange={updateBoard(i, 8)} /> </td> */}
                </tr>
            )
        }
        return (
            <table>
                <tbody>
                    {boardTableRows}
                </tbody>
            </table>
        )
    }

    console.log(state.board)
    return (
        <div>
            {makeBoardTable()}
        </div>
    )
}


export default Page;
